    class Program
    {
        static void Main(string[] args)
        {
            

            //--
            
            Console.WriteLine("Convertir varBinary a imagen");
            string varBinImg = "";
            stringToImage(varBinImg);

            var bytesArr = ConvertToImage(varBinImg);
