        public static void stringToImage(string stringFromSQL) {
            //Console.WriteLine("Convertir varBinary a imagen");
            //string varBinImg = "";


            var bytesArr = ConvertToImage(stringFromSQL);
            var image = byteArrayToImage(bytesArr);

        }




        public static byte[] ConvertToImage(string stringFromSQL)
        {
            //string stringFromSQL = "0x6100730064006600";
            List<byte> byteList = new List<byte>();

            string hexPart = stringFromSQL.Substring(2);
            for (int i = 0; i < hexPart.Length / 2; i++)
            {
                string hexNumber = hexPart.Substring(i * 2, 2);
                byteList.Add((byte)Convert.ToInt32(hexNumber, 16));
            }

            byte[] original = byteList.ToArray();
            return original;
        }

        public static Image byteArrayToImage(byte[] byteArrayIn)
        {
            try
            {
                using(MemoryStream ms = new MemoryStream(byteArrayIn)){ 
                
                    //MemoryStream ms = new MemoryStream(byteArrayIn);
                    Image returnImage = Image.FromStream(ms);
                    returnImage.Save("E:\\NetCORE Projects\\BinaryToImage\\img.png", ImageFormat.Png);
                
                    return returnImage;
                }

            }
            catch (Exception ex)
            {
                Console.WriteLine(ex);
                throw;
            }
        }
    }
}
